CREATE TABLE type_weaknesses (
    types VARCHAR(50) PRIMARY KEY,
    normal DECIMAL(2, 1),
    fire DECIMAL(2, 1),
    water DECIMAL(2, 1),
    electric DECIMAL(2, 1),
    grass DECIMAL(2, 1),
    ice DECIMAL(2, 1),
    fighting DECIMAL(2, 1),
    poison DECIMAL(2, 1),
    ground DECIMAL(2, 1),
    flying DECIMAL(2, 1),
    psychic DECIMAL(2, 1),
    bug DECIMAL(2, 1),
    rock DECIMAL(2, 1),
    ghost DECIMAL(2, 1),
    dragon DECIMAL(2, 1),
    dark DECIMAL(2, 1),
    steel DECIMAL(2, 1),
    fairy DECIMAL(2, 1)
);

CREATE TABLE base_stats (
    pkmn_name VARCHAR(30) PRIMARY KEY,
    type_1 VARCHAR(10) NOT NULL, 
    type_2 VARCHAR(10), 
    base_hp INTEGER NOT NULL, 
    base_attack INTEGER NOT NULL, 
    base_special_attack INTEGER NOT NULL, 
    base_defense INTEGER NOT NULL, 
    base_special_defense INTEGER NOT NULL, 
    base_speed INTEGER NOT NULL
);



